new Vue({el:"table",data:()=>({products:[],searchQuery:"",searchResultsCount:0}),watch:{searchQuery(t){t?this.products.forEach(e=>{e.hidden=-1==e.name.toLowerCase().indexOf(t.toLowerCase())}):this.products.forEach(t=>{t.hidden=!1}),this.searchResultsCount=this.products.filter(t=>!0!==t.hidden).length}},created(){fetch("stock.json").then(t=>t.json()).then(t=>{this.products=t,this.searchResultsCount=t.length}).catch(t=>console.log(t))},filters:{toCurrency:t=>new Intl.NumberFormat("hu-HU",{style:"currency",currency:"HUF",minimumFractionDigits:0}).format(t),toInt:t=>parseInt(t)}});